mapper.update:
UPDATE re_board SET
rw_content=#{rw_content}
WHERE rw_no=#{rw_no}

UPDATE re_board SET
rw_delete='y'
WHERE rw_no=#{rw_no}

UPDATE re_board SET
rw_content='관리자에 의해 삭제된 댓글입니다.'
WHERE rw_no=#{rw_no}

UPDATE re_board SET
depth=depth-1
WHERE rw_no=#{rw_no}

mapper.select:
SELECT group_id,group_step,group_tab
FROM re_board
WHERE rw_no=#{rw_no}

mapper.update:
UPDATE re_board SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE re_board SET
depth=depth+1
WHERE rw_no=#{rw_no}
